import React from 'react'

export default class TeamSelector extends React.Component {

    getCurrentLeague() {
        return document.querySelector('input[name="league"]:checked');
    }

    getTeamsList() {
        console.log(this.getCurrentLeague());
        var options = [];
        var ligue1Names = ['Select a team...', 'Amiens', 'Angers', 'Bordeaux', 'Berst', 'Dijon', 'Lille', 'Lyon', 'Marseille', 'Metz', 'Monaco', 'Montpellier', 'Nantes', 'Nice', 'Nîmes', 'Paris', 'Reims', 'Rennes', 'Saint-Etienne', 'Strasbourg', 'Toulouse'];
        if (this.props.league == 'Ligue 1') {
            teamNames = ligue1Names;
        }
        for (var i = 0; i < teamNames.length; i++) {
            options.push(<option key={i} value={teamNames[i]}>{teamNames[i]}</option>);
        }

        return options;
    }

    onChange(event) {
        var teamChosed = event.target.options[event.target.selectedIndex];
    }

    render() {
        return(
            <div className='team-selector'>
                <p>{this.props.value}</p>
                <select id={this.props.id} onChange={this.onChange}>
                    {this.getTeamsList()}
                </select>
            </div>
        )
    }
}
